SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;apres plusieurs divisions, on obtient des arteres de petit calibre, les arterioles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on y trouve de 1 a 5 couches de muscle lisse circonferentiel&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elles jouent un role fondamental dans l'amortissement donc la baisse du niveau de pression arterielle et le passage d'un regime de pression pulsatile a un regime plus continu. elles sont richement innervees et sont le siege principal des resistances vasculaires dans le systeme arteriel, comme nous le reverrons.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
