SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous avez ici resume les caracteristiques histologiques en bas avec les proportions en fibres elastiques et collagenes et en cellules musculaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;parallelement a l'augmentation de la proportion de cellules musculaires, on voit la capacite du vaisseau a se contracter ou se dilater. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme on peut s'y attendre, cette vasomotricite est significative au niveau des arteres musculaires et tres importante au niveau des arterioles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
