SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;interessons nous maintenant au regime de pression dans le systeme arteriel.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pression arterielle est la colonne de sang sur la paroi des arteres.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on l'exprime en mmhg ou en &lt;/span&gt;&lt;span &gt;cmhg&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;contrairement aux termes utilisees dans le langage courant, il s'agit d'une pression et non d'une tension.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pression oscille entre un maximum, la pression arterielle systolique et un minimum la pression arterielle diastolique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on definit aussi deux autres pressions&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une pression pulsee ou pression differentielle comme etant la difference entre les pressions arterielles systoliques et les pressions arterielles diastoliques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une pression moyenne : cette pression est une pression fictive qui est la pression constante qui regnerait dans l'artere si l'ecoulement n'etait pas pulsatile mais laminaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe enfin une incisure sur la partie descendante de la courbe, l'incisure dite &lt;/span&gt;&lt;span &gt;catacrote&lt;/span&gt;&lt;span &gt;, qui correspond au moment ou la valve aortique se ferme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@pad - effet windkessel@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide10.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'elasticite de la paroi arterielle joue un role physiopathologique majeur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lors de chaque ejection systolique, le ventricule gauche injecte dans l'aorte (chez un adulte au repos) environ 70 a 80 ml de sang, mais ce volume ne peut s'ecouler dans les tissus pendant le temps de la systole. il est donc, en grande partie, stocke temporairement grace a la dilatation de l'aorte et des grosses arteres, pour etre restitue en diastole. c'est l'effet windkessel (ainsi appele par analogie a un systeme de pompe a eau). ce phenomene survient tout au long de l'arbre arteriel et est responsable de la pulsatilite des arteres. c'est aussi grace a ce phenomene que l'on peut palper un pouls au niveau des arteres.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@caracteristiques @";
SDpagedocid[SDpagenum] = SDdocnum;
