SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide11.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous avons deja vu ce schema d'evolution de la pression arterielle le long du systeme arteriel.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le volume sanguin, en passant du vg a l'aorte, voit sa pression passer de 120/5 mmhg a 120/80 mmhg environ… &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les proprietes des differents vaisseaux vont conditionner l'evolution le long de l'arbre arteriel. vous visualisez ici tres bien le gradient de pression entre l'entree du systeme et la fin du systeme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@l'onde de pouls@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide12.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la deformation de la paroi arterielle generee par l'ejection se propage le long de l'arbre arteriel comme un onde, que l'on peut detecter en peripherie :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est le pouls, parfaitement palpable sur les arteres superficielles comme l'artere radiale et que l'on utilise pour mesurer la frequence cardiaque par exemple.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette onde de pouls se propage a une vitesse de quelques metres par seconde, d'abord lentement sur l'aorte (2 a 4 m/s), plus rapidement sur les arteres des membres superieurs (6 a 8 m/s), et plus encore sur les arteres des membres inferieurs (8 a 10 m/s), et d'autant plus vite que l'on s'eloigne du cœur. un exemple de la forme et de la vitesse de cette onde est montree sur le schema en bas a droite de la diapositive.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;attention il s'agit de la vitesse de l'onde parietale mais en aucun cas de la vitesse circulatoire, c'est-a-dire de la vitesse du sang a l'interieur du vaisseau.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la vitesse de propagation se fait par la paroi arterielle et est d'autant plus rapide que la paroi est epaisse et plus rigide, comportant une couche musculaire plus importante, expliquant que cette onde s'accelere a mesure que l'on s'eloigne de l'aorte. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la mesure de la vitesse de l'onde de pouls (que l'on mesure entre la carotide et la femorale) est un marqueur de rigidite de la paroi qui lui-meme est un marqueur du risque cv que l'on utilise en pratique courante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@onde de pouls - onde reflechie@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide13.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces ondes de pouls se reflechissent sur les bifurcations arterielles et se propagent, toujours le long de la paroi arterielle, mais de facon retrograde, majorant de quelques mmhg l'onde systolique : c'est ce que l'on appelle la pression augmentee, c'est-a-dire l'amplitude de l'onde reflechie. plus une artere est rigide, plus le signal se transmet rapidement et amplement, et plus la pression augmentee est importante. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une autre facon de l'exprimer et de calculer l'index d'augmentation (note ai) qui est le rapport de la pression augmentee ap sur la pression pulsee. on appelle aussi cette pression la pression centrale qui est un composant important de la post-charge ventriculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pa mesuree au bras en peripherie n'est donc pas toujours un bon reflet de la pa centrale donc de la postcharge ventriculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

