SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la mesure de la pa peut se faire de facon directe, c'est a dire en mettant un catheter dans une artere. c'est une methode invasive qu'il n'est bien sur pars possible d'utiliser en pratique clinique courante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on utilise donc des methodes indirectes mais fiables &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la methode palpatoire mais surtout la methode auscultatoire qui est plus precise. elle necessite un brassard relie a un manometre a pression et un stethoscope.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
