SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous verrons dans le chapitre suivant que la pa est une valeur tres regulee dans le systeme cv. elle peut toutefois varier dans diverses situations.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on distingue &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des variations non cycliques donc aigues, paroxystiques, liees a &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un stress physiques ou psychosensoriels (emotions, douleurs…)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des facteurs endogenes (hormones)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ou encore des facteurs exogenes (changement de position)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des variations cycliques :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a court terme liees a la balance &lt;/span&gt;&lt;span &gt;sympathovagale&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ou a plus long terme responsables de &lt;/span&gt;&lt;span &gt;variation saisonnieres, nycthemerales, de l'augmentation de la pa avec l'age ou de l'hypotension que l'on observe en &lt;/span&gt;&lt;span &gt;post-prandiale&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
