SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous avez represente ici un enregistrement de la pa sur toute une journee mesuree toutes les 15 minutes la journee et toutes les 30 minutes la nuit.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme vous pouvez le voir les pa systoliques et diastoliques sont variables d'un moment a l'autre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de facon physiologique, la pa diminue la nuit normalement de 10 a 20 %&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
