SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous ne reviendrons pas sur l'anatomie des gros troncs coronaires que vous avez vu en anatomie. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme vous le savez ces arteres sont situees sur la surface epicardiaque du cœur et donnes des branches intra-myocardiques jusqu'a l'endocarde, donnant un aspect peigne (comme vous pouvez le voir sur la figure en haut a droite de cette diapositive).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les echanges se font au niveau de capillaires continus comme nous l'avons vu dans le chapitre precedent.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le reseau coronaire est fortement innerve comme on peut le voir sur la photo en bas a droite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
