SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;outre les phenomenes precedents, les principaux facteurs de controle du debit coronaire sont les facteurs de controle des resistances et se situent au niveau metabolique, endothelial et par le systeme nerveux autonome.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
