SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il faut retenir de ce cours que la pression qui regne dans le systeme arteriel est elevee et qu'elle va s'amortir progressivement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pa a une valeur limite superieure au-dela de laquelle une intervention therapeutique s'impose car on ne peut imaginer des echanges au niveau capillaire que si la pression est relativement basse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous retiendrez enfin que la circulation coronaire est autoregulee ce qui permet de rendre le debit independant de la pa jusqu'a des valeurs relativement basses ce qui protege le myocarde dans certaines situations critiques et que la regulation du debit est surtout liee a des variations des resistances.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;bon courage et merci pour votre attention.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
