SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le cœur est un organe automatique et le muscle cardiaque se contracte donc regulierement. cela signifie qu'il n'a pas besoin de nerfs moteurs pour se contracter, comme c'est le cas pour les muscles stries squelettiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il est malgre tout richement innerve ce qui permet de controler et de moduler son fonctionnement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans ce premier chapitre, nous allons voir comment est generee et modulee l'automaticite cardiaque et comment cette activite se propage dans toutes les cavites.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
