SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;a la fin de ce chapitre, vous devrez etre capable :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de decrire les caracteristiques anatomiques et electrophysiologiques du tissu electrogenique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'expliquer l'origine de l'automaticite cardiaque et de decrire la conduction du potentiel d'action dans le cœur &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de connaitre&lt;/span&gt;&lt;span &gt; l'influence du systeme nerveux autonome sur l'automaticite et la conduction&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et enfin de connaitre les principales anomalies de l'automaticite et de la conduction&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
