SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le tissu electrogenique est un tissu specialise situe au sein du myocarde.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les cellules qui le composent ont la meme origine embryologique que les cardiomyocytes et comportent d'ailleurs quelques myofibrilles bien qu'elles soient peu contractiles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le tissu electrogenique a deux proprietes specifiques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il genere automatiquement des impulsions electriques c'est a dire des potentiels d'action - c'est ce que l'on appelle &lt;/span&gt;&lt;span  textdecoration='underline'&gt;l'automaticite&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et, il transmet ces impulsions a l'ensemble des structures cardiaques, c'est ce que l'on appelle la &lt;/span&gt;&lt;span  textdecoration='underline'&gt;conduction&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour cela, ce tissu comporte deux &lt;/span&gt;&lt;span &gt;types cellulaires&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cellules nodales (du nœud sinusal, nœud av et tronc du faisceau de his)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cellules specialises du tissu de conduction (branches du faisceau de his et cellules de purkinje)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
