SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces deux groupes cellulaires se distinguent bien.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontstyle='italic'&gt;les cellules nodales &lt;/span&gt;&lt;span &gt;sont plus petites que les cardiomyocytes, pauvres en myofibrilles et riches en grain de glycogene - c'est ce que vous pouvez voir sur cette coupe de myocarde en microscopie optique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontstyle='italic'&gt;les cellules de purkinje&lt;/span&gt;&lt;span &gt; sont elles beaucoup plus volumineuses que les cardiomyocytes leur cytoplasme est abondant, clair, riche en glycogene et en mitochondries, pauvre en myofibrilles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;du fait de leur grande taille, ces cellules offrent peu de resistance a la propagation des pa.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
