SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pourquoi ce potentiel de repos n'est-il pas stable dans les cellules nodales ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la derive du potentiel de repos, caracteristique des cellules automatiques dites pacemakers, est sous-tendue par differents courants ioniques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une dizaine de courants ioniques differents ont ete identifies mais les principaux evenements sont :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une fuite sodique responsable d'un petit courant sodique entrant (ib)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un courant entrant non selectif de k+, de ca&lt;/span&gt;&lt;span &gt;2+ &lt;/span&gt;&lt;span &gt;et surtout de na+, a travers un canal appele canal if mais qui se ferme a mesure que l'on approche du potentiel seuil et &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un courant calcique entrant transitoire, responsable de la derniere partie de la depolarisation avant l'atteinte du potentiel seuil +++&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
