SDpagenum++;
SDpagetitle[SDpagenum] = "@le potentiel d'action (pa)@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide14.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le potentiel d'action est la variation dans le temps du potentiel transmembranaire de la cellule cardiaque au cours d'un cycle cardiaque ; c'est donc un cycle electrique lie a des mouvements ioniques passifs et actifs&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;quels sont les principaux mouvements ioniques qui surviennent lors du potentiel d'action ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on distingue 5 phases, notes de 0 a 4&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

