SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous avez ici represente l'evolution des conductances ioniques en bas en fonction du temps et des differentes phases du potentiel d'action en haut&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous pouvez retrouver les differents courants ioniques decrits precedemment mais il faut bien comprendre que ce sont des phenomenes dynamiques et qu'a un instant t, l'effets des differents courants ioniques se superposent.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@principaux courants ioniques@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide16.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;sur ce schema du potentiel d'action, vous avez symbolise les mouvements ioniques des 3 principaux ions. le sens de la fleche indique un courant entrant quand elle est dirigee vers l'interieur de du pa et un courant sortant quand la fleche est dirigee vers l'exterieur du pa.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la phase 0 est la phase dite de depolarisation rapide. pendant cette phase il y a une ouverture des canaux sodiques qui est quantitativement le principal evenement de cette phase ; les canaux potassiques se ferment et les canaux calciques vont progressivement s'ouvrir ;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la phase 1 est la phase toute initiale de la repolarisation ; c'est une phase tres courte liee a un courant sortant de potassium et a la fermeture des canaux sodiques ;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;survient alors la phase 2 dite de plateau &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; cette phase stable est liee a un courant entrant calcique important equilibrant un courant potassique sortant. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la phase 3 est la phase de repolarisation liee a une fermeture des canaux calciques et a l'ouverture des canaux potassiques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin la phase 4 correspond au potentiel de repos, stable dans cette exemple, ou les pompes na/k retablissent et maintiennent un equilibre ionique, avant la survenue de la prochaine depolarisation declenchee par les cellules automatiques. c'est un phenomene cyclique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;cette diapositive vous montre la coexistence dans le temps ne nombreux courants ioniques dont la resultante est le potentiel d'action. je ne vous demande evidemment pas de la connaitre - seuls les evenements principaux de la diapositive precedente doivent etre connus.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

