SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la conduction se fait par le tissu electrogenique mais aussi principalement de proche en proche par les cardiomyocytes eux-memes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les cardiomyocytes sont entrelacees entre elles et fortement adherentes les unes aux autres par l'intermediaire de disques intercalaires qui comprennent deux types de jonction intercellulaires : les desmosomes et les gap junctions (encore appeles jonctions communicantes) &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces derniers sont des zones de forte conductance electrique permettant une propagation rapide des potentiels d'action d'une cellule a l'autre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un cardiomyocyte est relie aux cellules voisines en moyenne par un dizaine de jonctions intercellulaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
