// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@controle de l'automaticite et de la conduction@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide23.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le cœur est un organe automatique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il est toutefois richement innerve par le sna, organise en neurones conduisant les informations centrales vers le cœur et modulant son fonctionnement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le systeme &lt;/span&gt;&lt;span  textdecoration='underline'&gt;sympathique&lt;/span&gt;&lt;span &gt; fait relai dans les ganglions cervicaux et les ganglions thoraciques jusqu'en t4 &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; ce sont les nerfs cardiaques. en atteignant le cœur, on assiste a une ramification terminale dans le pericarde, le myocarde et l'endocarde des 4 cavites cardiaques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le systeme &lt;/span&gt;&lt;span  textdecoration='underline'&gt;parasympathique&lt;/span&gt;&lt;span &gt; chemine par le nerf vague (nerf vague droit pour le nœud sinusal et nerf vague gauche pour le nœud av). il se ramifie surtout autour du tissu electrogenique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@effets d'une stimulation sympathique@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide24.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;lors d'une stimulation sympathique (par exemple l'injection de catecholamines ou une emotion forte), plusieurs modifications sont notables sur l'evolution du potentiel d'action d'une cellule nodale : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le potentiel de repos est plus haut, la membrane est donc deja pre-depolarisee &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; mais surtout la vitesse a laquelle la membrane se depolarise spontanement, donc la vitesse a laquelle on atteint le potentiel seuil est plus importante &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; le cycle de depolarisation-repolarisation est plus court donc la frequence de decharge du nœud sinusal est plus importante&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces effets sont surtout dus a la stimulation des recepteurs beta-adrenergiques, qui augmente le temps d'ouverture du canal if&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@effets d'une stimulation sympathique@";
SDpagedocid[SDpagenum] = SDdocnum;
