SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide25.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;lors d'une stimulation sympathique on observe donc :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une augmentation de l'excitabilite &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; la difference entre le potentiel de repos et le potentiel seuil est plus petite, donc on peut facilement declencher un potentiel d'action : on parle d'effet bathmotrope positif &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; le revers de la medaille est que l'on peut declencher plus facilement des troubles du rythme, des palpitations, puisque le cœur est facilement excitable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une acceleration de l'automaticite et on parle d'effet chronotrope positif&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les deux effets decrits precedemment doivent logiquement s'accompagner d'une amelioration de la conduction et d'un raccourcissement des periodes refractaires, on parle d'effet dromotrope positif.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin vous savez deja sans doute que la stimulation sympathique s'accompagne aussi d'une augmentation de la force de contraction des cardiomyocytes et on parle d'effet inotrope positif&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@effets d'une stimulation parasympathique@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide26.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les effets d'une stimulation parasysmpathiques vont etre presque opposes a ceux de la stimulation sympathique avec :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un potentiel de repos cette fois-ci plus bas : on parle d'hyperpolarisation de la membrane&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et une vitesse de depolarisation spontanee plus lente &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; le cycle de depolarisation-repolarisation est plus long donc la frequence de decharge du nœud sinusal est plus lente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une stimulation parasympathique peut-etre obtenue par injection d'acetylcholine ou observee lors d'une stimulation dite vagale comme lors d'un massage du glomus carotidien.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces effets sont surtout dus a la stimulation des recepteurs muscariniques qui augmente la conductance potassique responsable de l'hyperpolarisation membranaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

