SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'influence des systemes sympathique et parasympathique est evidemment variable, autours de la frequence de decharge spontanee du nœud sinusal, que l'on appelle aussi la frequence intrinseque cardiaque. celle-ci est &lt;/span&gt;&lt;span &gt;aux alentours de 100 par minute mais il s'agit d'une caracteristique electrophysiologigue propre a chaque individu.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme vous pouvez le voir sur ces deux schemas, le systeme parasympathique predomine dans les basses frequences et son influence disparait aux hautes frequences et inversement pour le systeme sympathique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
