SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les anomalies ou particularites de l'automaticite regroupent toutes les situations ou le rythme cardiaque n'est plus le rythme sinusal, c'est a dire quand le nœud sinusal n'est pus le pacemaker physiologique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le rythme jonctionnel est frequent &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; dans ce cas c'est le nœud av qui est le pacemaker physiologique &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; on l'observe facilement chez le sportif entraine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le rythme idioventriculaire, avec donc un echappement bas situe, est une situation pathologique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, en pathologie et pour diverses raisons, un foyer situe dans les oreillettes (droite ou gauche) peut se depolariser spontanement et plus rapidement que les cellules nodales &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; il peut donc prendre le dessus et imposer son rythme qui sera donc generalement rapides&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
