SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les anomalies de la conduction peuvent etre isolees ou associees a des anomalies de l'automaticite&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;certaines sont frequentes et peuvent etre considerees comme des variantes de la normale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on peut observer des anomalies de conduction entre les oreillettes, rares et souvent difficiles a mettre en evidence. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les deux autres anomalies sont beaucoup plus frequentes &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on decrit des anomalies de conduction entre les oreillettes et les ventricules et on parle de bloc auriculo-ventriculaires, abreges par l'acronyme bav, qui peut etre plus ou moins complet&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin les anomalies de conduction sur les branches du faisceau de his sont tres frequentes, surtout le bloc de la branche droite que l'on retrouve dans 20 % de la population dans sa forme incomplete.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
