SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme les autres disciplines, la rythmologie (qui est l'etude des troubles du rythme cardiaque) a largement beneficie de la revolution du genome. les genes codant pour la plupart des canaux ioniques ont ete identifies.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de plus en plus de mutations sur des genes, responsables d'anomalies de la structure et de la fonction de differents canaux ioniques sont responsables d'anomalie de l'automaticite et/ou de la conduction.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un apercu est donne dans ce tableau avec l'anomalie clinique ou electrocardiographique correspondante. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'agit evidemment d'une information pour vous sensibiliser aux implications pratiques de ce que vous avez appris dans ce cours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en aucun cas, le contenu de ce tableau n'est a connaitre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
