SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;des lors qu'un organisme est compose de nombreuses cellules, il faut, pour un fonctionnement harmonieux, un systeme qui permette d'homogeneiser le milieu interieur, d'echanger des informations et d'apporter differents elements aux cellules pour leur metabolisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le systeme cardio-circulatoire est un systeme &lt;/span&gt;&lt;span  textdecoration='underline'&gt;d'echange&lt;/span&gt;&lt;span &gt; entre les differents organes pour permettre un fonctionnement optimal du corps dans son integralite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous decrirons successivement et separement les fonctionnements du cœur et du systeme circulatoire mais dans l'organisme, le systeme cardio-circulatoire est evidemment en interaction permanente avec les autres organes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@organisation generale@";
SDpagedocid[SDpagenum] = SDdocnum;
