SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le systeme cardio-circulatoire est un systeme dit de &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;convection&lt;/span&gt;&lt;span &gt; (c'est-a-dire de transport) qui s'oppose aux systemes dit eux de diffusion, que l'on observe par exemple au niveau respiratoire ou s'effectue des echanges gazeux et au niveau de tous les tissus peripheriques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
