SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;on peut schematiquement distinguer 4 fonctions essentielles au systeme cardio-circulatoire :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;repondre en toute situation aux besoins metaboliques des organe qui peuvent varier dans le temps de facon majeure;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;transferer des signaux entre les cellules et vehiculer des substances specifiques comme par exemple des nutriments, des gaz ou encore des hormones ;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;transferer de la chaleur depuis les lieux de production jusqu'a son elimination, role essentiel dans la thermoregulation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
