SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la pression arterielle oscille entre un maximum, la pas, et un minimum, la pad.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pam est un parametre qui integre ces deux valeurs et qui oscille au repos, chez un sujet jeune sain, autour de 100 mmhg&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si on s'interesse aux determinants principaux de ce parametre, vous savez maintenant que la pam est le produit du debit cardiaque (note qc) et des resistances arterielles systemiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le debit cardiaque est controle par plusieurs facteurs : les besoins metaboliques des organes peripheriques, la frequence cardiaque et le volume d'ejection systolique lui-meme sous la dependance du remplissage, de la contractilite et de la volemie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les resistances arterielles systemiques sont elles surtout dependantes du diametre des arterioles donc sous la dependance de la vasomotricite et a un moindre degre de la viscosite sanguine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous devez etre parfaitement a l'aise maintenant avec ces concepts.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@le tonus vasomoteur basal@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il existe a l'etat basal un tonus sympathique vasoconstricteur sur les arterioles. cela signifie que les arterioles sont en etat stable un peu contracte. cet etat est lie a la stimulation des recepteurs alpha-1 adrenergiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est un element important a garder a l'esprit car cela permet deux degres de liberte : relacher le tonus vasomoteur, ce qui conduit a une vasodilatation ou accentuer le tonus vasomoteur ce qui accentue la vasoconstriction &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si ce tonus n'existait pas, les arterioles seraient dilatees a l'etat basal et la seule possibilite serait qu'elles se contractent. il n'y aurait donc aucune possibilite de faire dilater les arteres donc aucune possibilite de faire diminuer les resistances arterielles&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce sont les 3 situations du haut sur cette diapositive ou on peut voir un neurone en bleu avec son activite rythmique schematisee par les traits.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un autre degre de regulation est la possibilite sur certains vaisseaux de faire &lt;/span&gt;&lt;span  fontweight='bold'&gt;se dilater &lt;/span&gt;&lt;span &gt;les arterioles en stimulant d'autres recepteurs, les recepteurs beta &lt;/span&gt;&lt;span  fontweight='bold'&gt;2&lt;/span&gt;&lt;span &gt;-adrenergiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les mecanismes de regulation@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est classique de distinguer differents mecanismes de regulation en fonction de son delai d'action.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cela permet d'essayer de reagir face a une situation aigue, comme par exemple une chute de la pa lors d'une hemorragie ou au contraire face a une elevation brutale de la pa, par exemple lors d'un stress important. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais si le niveau de pression arterielle est elevee de facon chronique, d'autres mecanismes vont se mettre en place.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette redondance des systemes permet aussi de palier a une eventuelle defaillance ou a un depassement de l'un ou l'autre de ces systemes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on distingue ainsi classiquement :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une regulation a court terme, cycle a cycle, sous la forme d'une boucle reflexe et que l'on appelle le baroreflexe&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une regulation a moyen terme (plusieurs minutes a quelque heures) qui est un mecanisme hormonal par le systeme renine-angiotensine-aldosterone, et enfin&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une regulation a long terme (de plusieurs heures a quelques jours) qui est une regulation renale de l'equilibre hydro-sode.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
