SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la sensibilite du baroreflexe est variable d'un sujet a l'autre et peut-etre variable en pathologie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'agit ici de la relation pam - frequence cardiaque. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plus le baroreflexe est sensible, plus la reponse qu'il engendre est quantitativement importante. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous verrons que les effecteurs sont multiples mais si on prend la fc, plus la pam varie, plus les variations de fc sont importantes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on peut mesurer la sensibilite du baror en clinique dans les anomalies du controle neuro-cardiovasculaire par le sna que l'on appelle les dysautonomies.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
