SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les signaux enregistres et inities par la stimulation des barorecepteurs cheminent jusqu'aux centres vasomoteurs bulbaires par &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le nerf de &lt;/span&gt;&lt;span &gt;hering&lt;/span&gt;&lt;span &gt;, branche du nerf glossopharyngien pour les barorecepteurs carotidiens et&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le nerf de &lt;/span&gt;&lt;span &gt;ludwing&lt;/span&gt;&lt;span &gt;-cyon, branche du nerf vague, pour les barorecepteurs aortiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
