SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces signaux sont integres au niveau des centres vasomoteurs du tronc cerebral.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce centre est constitue de plusieurs structures : on distingue le centre vasomoteur qui est compose d'un sous-centre vasodilatateur et d'un sous-centre vasoconstricteur, et le centre cardio-inhibiteur dit egalement cardio-modulateur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les nerfs efferents du reflexe sont les nerfs parasympathiques et sympathiques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
