SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;enfin, et vous avez la la boucle baroreflexe complete, la reponse est organisee avec des efferences sympathiques sur le cœur et les vaisseaux et des efferences parasympathiques uniquement sur le cœur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
