// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@stimulation sympathique surrenalienne@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide15.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le baroreflexe que nous avons decrit organise aussi sa reponse en stimulant la medullo-surrenale et en liberant deux hormones la noradrenaline et l'adrenaline. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les effets de ces deux hormones sur le cœur et les vaisseaux sont environ 10 fois plus longs que ceux de la noradrenaline liberee par les nerfs sympathiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
