SDpagedisplay[SDpagenum] = "Slide19.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;enfin, a plus long terme, la regulation de la pression arterielle se fait par la regulation de la volemie, donc du volume circulant, par le rein.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez vu ou verrez en detail ces mecanismes dans le cours de physiologie renale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
