// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@regulation renale@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide20.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le controle renal est a la fois indirect, par l'intermediaire d'une stimulation du sraa, comme nous l'avons vu precedemment mais aussi direct. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tres schematiquement, une augmentation de la pression arterielle entraine une augmentation du debit urinaire (ce que l'on appelle la diurese pressive), contribuant a une baisse de la volemie, donc du retour veineux, donc du debit cardiaque.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une baisse de la volemie participe donc a la baisse de la pression arterielle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
