SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;finalement, si tous les elements de ce cours sont importants, il faut retenir&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;que la pa est regulee par differents systemes, parfois intriques qui se mettent en route avec des delais differents et qui induisent des reponses variables en amplitude et en duree.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;que les cibles des differents systemes sont multiples mais principalement situes sur les vaisseaux (en jouant sur la vasomotricite et les resistances), la volemie et le debit cardiaque.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;merci pour votre attention.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
