SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;apres le pole veineux des capillaires, le sang arrive dans des vaisseaux que l'on appelle les veinules.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces vaisseaux qui ont entre 0,2 et 1 mm de diametres sont generalement peu muscularisees mais possedent quand meme des proprietes de vasoconstriction. il est donc possible de modifier leur diametre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
