SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les veinules se regroupes et se jettent dans des vaisseaux de plus gros calibres, les veines dont la reunion donnera des vaisseaux de plus en plus gros. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une des grandes difference entre arteres et veines de meme diametre est dans la taille relative de la media, moins importante dans les veines. elles contiennent peu de fibres elastiques, les rendant tres distensibles. ce sont des vaisseaux capacitifs, capables de se deformer et de stocker un volume sanguin important, mobilisable en cas de besoin, comme nous le reverrons.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on retrouve aussi des fibres collagenes, surtout dans les veines abdominales et les veines des membres inferieurs, ce qui rend ces vaisseaux moins deformables.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

