SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les veines deviennent de plus en plus grosses pour se terminer dans l'oreillette droite par la veine cave superieure qui ramene le sang de la partie superieure du corps (essentiellement les membres superieures et la tete) et par la veine cave inferieure qui ramene le sang de a partie inferieure du corps (essentiellement les membres inferieurs et les organes de la cavite abdominale).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des que le diametre est important, on voit apparaitre des fibres musculaires et la paroi des veines est de nouveau vascularise par des vasavasorum pour ses couches externes, comme pour les arteres de gros calibre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;evidemment toutes les veines sont aussi recouvertes d'un endothelium et les certaines veines et les gros troncs ont la particularite d'&lt;/span&gt;&lt;span &gt;etre valvules&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
