SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la circulation veineuse a deux roles essentielles&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tout d'abord, elle doit assurer le retour veineux dont le debit est egal au debit cardiaque&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ensuite, parce que la circulation veineuse est un reservoir, elle peut mobiliser une partie plus ou moins importante de volume sanguin circulant contenu dans les veines, pour optimiser l'hemodynamique. c'est donc un reservoir en volume, que l'on appelle aussi un reservoir volemique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
