SDpagedisplay[SDpagenum] = "Slide13.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le retour veineux est un debit.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;conformement aux lois de l'hemodynamique, le debit du retour veineux est proportionnel au gradient de pression entre les capillaires et l'oreillette droite et inversement proportionnel aux resistances veineuses.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le gradient de pression, que l'on appelle la pression dynamique, est la pression motrice de la circulation veineuse. elle est d'environ 8 mmhg &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle ne permet donc pas a elle seule d'assurer le retour sanguin vers l'od, surtout pour le rv de la partie inferieure du corps qui, en position debout, doit aussi vaincre la gravite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plus que la pression capillaire, on definit dans le systeme circulatoire une pression systemique moyenne, que nous avons deja vu, qui, je vous le rappelle, est la pression qui regnerait dans le systeme circulatoire immediatement apres un arret circulatoire, apres egalisation des pressions dans tout le systeme. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vue les conditions hemodynamiques spontanees peu favorables au retour veineux, d'autres facteurs doivent y contribuer et le favoriser.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons les voir dans les prochaines diapositives &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
