SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous avez represente ici l'influence de la pression moyenne de remplissage systemique sur le retour veineux&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque la pression de remplissage systemique augmente pour une meme pression auriculaire droite, le gradient de pression augmente et le debit du retour veineux augmentera (ici en vert sur la figure) et inversement pour une baisse de la pression de remplissage systemique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la relation a la meme forme qu'avant avec une annulation theorique du retour lorsque la pression de remplissage systemique est egale a la pression dans l'oreillette droite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
