SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voici un exemple de mesure de pression veineuse chez un individu qui commence par rester immobile : la pression veineuse &lt;/span&gt;&lt;span &gt;est proche de 90 mmhg du fait du poids de la colonne de sang.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque la personne commence a marcher, la pression baisse pour se stabiliser aux alentours de 30 mmhg. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si la personne arrete de marcher en 20 secondes environ la pression veineuse revient a la valeur proche de 90 mmhg.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;donc si vous etes debout, le mieux pour favoriser le retour veineux c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est de marcher. il s'agit evidemment de la pression entre deux contractions musculaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si vous ne bougez pas, vous pouvez avoir a la fois la sensation de jambes « lourdes » et eventuellement de petite oedemes au niveau des chevilles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
