SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en inspiration, le diaphragme s'abaisse &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pression intra-thoracique diminue favorisant le rv de la partie superieurs du corps alors que la pression intra-abdominale augmente diminuant le rv de la partie inferieure du corps (des membres inferieurs) mais favorisant la chasse du sang intra-abdominal. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la baisse de la pression intra-thoracique se transmet a l'od qui voit sa pression diminuee, favorisant donc le retour veineux. ce surplus de remplissage sera pris en charge par le cœur qui s'accelerera.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les phenomenes s'arretent a l'expiration. le rv diminue, revenant a la valeur basale, et le cœur se ralentit.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe donc un veritable cycle respiratoire du retour veineux parfois favorise pour les membres inferieurs, parfois plutot favorable a la partie superieure du corps.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
