SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous avions deja vu dans l'organisation generale du systeme circulatoire, les rapports de la circulation lymphatique avec la circulation systemique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je vous rappelle que la circulation lymphatique prend son origine a l'interieur des tissus. les differents vaisseaux lymphatiques vont se reunir pour former deux gros vaisseaux qui se jettent dans le reseau veineux juste en amont du cœur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
