SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce systeme permet de prendre en charge le liquide et les macromolecules qui n'ont pu etre reabsorbees le long du capillaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on observe un mouvement de liquide vers les capillaires lymphatiques des lors qu'il existe une augmentation de pression a l'interieur de l'&lt;/span&gt;&lt;span &gt;interstitium. il y a alors ouverture des jonctions intercellulaires et passage libre de liquides et de macromolecules dans les capillaires. une fois que les capillaires sont remplis, la dilatation des cellules myoepitheliales provoque leur contraction rythmique et permet l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;ejection du liquide lymphatique dans les collecteurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
