SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le systeme lymphatique joue plusieurs roles importants. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il prend en charge le volume quotidien qui n'a pu etre pris en charge par la circulation sanguine. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le volume de liquide represente environ 3 l/j ce qui peut paraitre derisoire par rapport aux centaines de litres qui circulent dans le systeme en une journee, mais nous verrons les consequences d'un dysfonctionnement de cette propriete&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ils transportent des macromolecules comme des proteines de gros poids moleculaires, ou meme des cellules et c'est une voie de dissemination des cancers,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au niveau intestinal, c'est le systeme lymphatique qui va transporter les graisses (sous forme de chylomicrons)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, il a deux autres roles majeurs dans la protection de l'organisme que sont la filtration et la detection d'eventuels agents pathogenes dans les ganglions et il joue un role majeur dans l'immunite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
