SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans la cohorte retroprospective ou ambidirectionnelle, on associe les principes de la cohorte retrospective et de la cohorte prospective. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;aujourd'hui on consulte le fichier des salaries de l'entreprise de chantier naval de 2001.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a partir des informations de ce fichier, on repartit les salaries en deux cohortes : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une cohorte de sujets exposes a l'amiante en 2001 formee des salaries qui travaillaient dans les ateliers a l'epoque&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une cohorte de sujets non-exposes a l'amiante en 2001 constituee des salaries qui travaillaient dans les bureaux a l'epoque.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les 2 cohortes ont donc ete constituees retrospectivement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en revanche, on continue de suivre les sujets jusqu'en 2021 comme dans la cohorte prospective.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cela confere a l'etude une duree de suivi de 20 ans pour une duree effective d'etude circonscrite a l'intervalle entre aujourd'hui et 2021.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
