SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans une etude de cohorte, le risque relatif est la mesure d'association entre l'exposition et la maladie qu'il est naturel de calculer.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cependant, il est aussi possible de calculer l'odds ratio (qui peut etre calcule dans tout type d'etude epidemiologique).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le risque relatif est le rapport du risque de maladie chez les sujets exposes divise par le risque de maladie chez les sujets non-exposes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@ii.1. etude de cohorte@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide22.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le risque relatif estime dans une etude de cohorte est une estimation ponctuelle realisee sur un echantillon de sujets.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette valeur calculee sur un echantillon est probablement proche mais pas exactement egale a la valeur du risque relatif qui aurait ete calculee sur la population dont est issu cet echantillon. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cet ecart resulte des fluctuations d'echantillonnage (c'est-a-dire du hasard).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette estimation ponctuelle doit etre encadree d'un intervalle de confiance, le plus souvent a 95%.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un intervalle de confiance a 95% est l'intervalle dans lequel il y a 95% de chances que la vraie valeur du risque relatif de la population se situe. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ca n'est pas une definition tout a fait exacte de l'intervalle de confiance que je viens de donner mais elle a l'avantage de vous permettre de comprendre de quoi il s'agit. pour une definition plus formelle, il vous faut retourner au cours de statistique sur l'estimation du pr cinquin au 1&lt;/span&gt;&lt;span &gt;er&lt;/span&gt;&lt;span &gt; semestre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plusieurs formules approchees permettent de calculer les bornes inferieure et superieure de l'intervalle de confiance d'un risque relatif. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la formule donnee ici n'est pas a savoir pour l'examen.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;z&lt;/span&gt;&lt;span &gt;α&lt;/span&gt;&lt;span &gt; est la valeur lue sur la table de la loi normale centree reduite correspondant a &lt;/span&gt;&lt;span &gt;α&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour un intervalle de confiance a 95%, &lt;/span&gt;&lt;span &gt;α&lt;/span&gt;&lt;span &gt; est egal a 5% ou 0,05, la valeur correspondante de z est 1,96.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
