SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une etude cas-temoins compare la frequence d'une exposition dans le passe a un facteur de risque entre :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des sujets atteints de la maladie etudiee (les cas)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et des sujets indemnes de la maladie etudiee (les temoins)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le recueil d'information porte sur l'exposition et est toujours retrospectif.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;s'il existe une association entre la maladie et l'exposition, le pourcentage de sujets exposes sera plus important chez les cas que chez les temoins.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'etude cas-temoins compare des frequences d'exposition et non pas des risques de maladie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les mesures d'association basees sur le risque de maladie ne pourront donc pas etre utilisees dans les etudes cas-temoins. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans une etude cas-temoin, il n'est pas possible de calculer le risque relatif car on ignore le risque de maladie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
